import axios from 'axios'

export const addFav = (character) => {
   const endpoint = 'http://localhost:3001/rickandmorty/fav';
   return (dispatch, getState) => {
      const { myFavorites } = getState().reducer; // Asegúrate de reemplazar 'reducer' con el nombre real del reductor en tu aplicación
      const updatedFavorites = [...myFavorites, character];
      const uniqueFavorites = updatedFavorites.filter((item, index, self) =>
         index === self.findIndex((t) => (
            t.id === item.id && t.name === item.name
         ))
      );
      axios.post(endpoint, uniqueFavorites).then(({ data }) => {
         return dispatch({
            type: 'ADD_FAV',
            payload: uniqueFavorites,
         });
      });
   };
};



export const removeFav = (id) => {
   const endpoint = 'http://localhost:3001/rickandmorty/fav/' + id;
   return (dispatch) => {
      axios.delete(endpoint).then(({ data }) => {
         return dispatch({
            type: 'REMOVE_FAV',
            payload: data,
      });
      });
   };
};

export const filterCards = (gender) => {
    return {type: "FILTER", payload: gender}
};

export const orderCards = (order) => {
    return {type: "ORDER", payload: order}
};
