import axios from 'axios'

export const addFav = (character) => {
   const endpoint = 'http://localhost:3001/rickandmorty/fav';
   return (dispatch, getState) => {
      const { myFavorites } = getState().reducer; // Reemplaza 'reducer' con el nombre real del reductor en tu aplicación

      // Verificar si el personaje ya está en la lista de favoritos
      const isDuplicate = myFavorites.find((favCharacter) => favCharacter.id === character.id);
      if (isDuplicate) {
         console.log('El personaje ya está en la lista de favoritos.');
         return;
      }

      const updatedFavorites = [...myFavorites, character];

      // Actualizar el estado con los favoritos actualizados
      dispatch({
         type: 'ADD_FAV',
         payload: updatedFavorites,
      });

      // Enviar la solicitud POST al endpoint
      axios.post(endpoint, updatedFavorites).then(({ data }) => {
         console.log('Favoritos actualizados:', updatedFavorites);
      });
   };
};



export const removeFav = (id) => {
   const endpoint = 'http://localhost:3001/rickandmorty/fav/' + id;
   return (dispatch) => {
      axios.delete(endpoint).then(({ data }) => {
         return dispatch({
            type: 'REMOVE_FAV',
            payload: data,
      });
      });
   };
};

export const filterCards = (gender) => {
    return {type: "FILTER", payload: gender}
};

export const orderCards = (order) => {
    return {type: "ORDER", payload: order}
};
